import { Component } from '@angular/core';
import { Note } from 'src/app/core/model/note';
import { NoteService } from 'src/app/core/services/note.service';
@Component({
  selector: 'app-hero-page',
  templateUrl: './hero-page.component.html',
  styleUrls: ['./hero-page.component.scss'],
})
export class HeroPageComponent {
  notes: Note[] = [];

  constructor(public noteService: NoteService) {
    this.getListOfNotes();
  }

  getListOfNotes() {
    let model = {
      isCountOnly: false
    }
    this.noteService.listNotes(model).subscribe((resp) => {

    })
  }

  addNotes(id = '') {
    let newGroup = new Note();
    this.notes.push(newGroup);
  }

  removeNote(i: number) {
    this.notes.splice(i, 1);
  }

  updateList() {

  }
}
<div class="note-wrapper">
  <div class="btn-wrapper">
    <button mat-raised-button class="add-notes" color="primary" (click)="addNotes()">
      Add Note
    </button>
  </div>
  <div class="note-container">
    <div class="single-note" *ngFor="let note of notes; let i = index">
      <app-note [note]="note"></app-note>
    </div>
  </div>
</div>

