import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ChangeDetectorRef, Component, Input } from '@angular/core';
import { AngularEditorConfig } from '@kolkov/angular-editor';
import { Note } from 'src/app/core/model/note';
@Component({
  selector: 'app-note',
  templateUrl: './note.component.html',
  styleUrls: ['./note.component.scss'],
})
export class NoteComponent {
  form: FormGroup;
  @Input() note: Note = new Note();

  constructor(private fb: FormBuilder) {
    this.creteForm();
  }

  creteForm() {
    this.form = this.fb.group({
      description: [this.note.description, [Validators.required]],
      id: [this.note.id, [Validators.required]],
      title: [this.note.title, [Validators.required]],
    });
  }

  editorConfig: AngularEditorConfig = {
    editable: true,
    spellcheck: true,
    height: '20rem',
    minHeight: '3rem',
    placeholder: 'Enter your notes here...',
    translate: 'no',
    width: '100%',
    toolbarPosition: 'bottom',
    outline: false,
    showToolbar: true,
    uploadWithCredentials: false, // if needed
    customClasses: [
      // optional
      {
        name: 'quote',
        class: 'quote',
      },
      {
        name: 'redText',
        class: 'redText',
      },
      {
        name: 'titleText',
        class: 'titleText',
        tag: 'h1',
      },
    ],
  };

  removeNote() {

  }

  saveNote() {

  }

  // showToolbar() {
  //   this.editorConfig.showToolbar = true;
  //   this.cd.detectChanges();
  // }

  // hideToolbar() {
  //   this.editorConfig.showToolbar = false;
  //   this.cd.detectChanges();
  // }
}
<form [formGroup]="form">
  <div class="single-note">
    <div class="title-div">
      <input type="text" formControlName="title" placeholder="Title...">
      <span class="save-icon" (click)="saveNote()">
        <mat-icon>save</mat-icon>
      </span>
      <span class="close-icon" (click)="removeNote()">
        <mat-icon>close</mat-icon>
      </span>
    </div>
    <angular-editor formControlName="description" [id]="note?.id?.toString()" [config]="editorConfig"></angular-editor>
    <span class="save-btn"> </span>
  </div>
</form>

