import { Component } from '@angular/core';
import { Note } from 'src/app/core/model/note';
import { NoteService } from 'src/app/core/services/note.service';

@Component({
  selector: 'app-notes-page',
  templateUrl: './notes-page.component.html',
  styleUrls: ['./notes-page.component.scss'],
})
export class NotesPageComponent {
  notes: Note[] = [];
  pageNumber = 1;
  query = '';

  constructor(public noteService: NoteService) {
    this.getListOfNotes();
  }

  scrollNotesList() {
    this.pageNumber = this.pageNumber + 1;
    this.getListOfNotes();
  }

  getListOfNotes() {
    let model = {
      isCountOnly: false,
      options: {
        page: this.pageNumber,
        paginate: 6
      },
    };
    if (this.query) {
      model['query'] = {
        description: this.query
      }
    }
    this.noteService.listNotes(model).subscribe((resp: any) => {
      if (resp && resp.status == 'SUCCESS' && resp.data && resp.data.data) {
        this.notes = [...this.notes, ...resp.data.data];
      }
    });
  }

  addNotes(id = '') {
    let newGroup = new Note();
    this.notes.push(newGroup);
  }
}
<div class="note-wrapper">
  <div class="btn-wrapper">
    <button mat-raised-button class="add-notes" color="primary" (click)="addNotes()">
      Add Note
    </button>
  </div>
  <div class="note-container" (scroll)="scrollNotesList()">
    <div class="single-note" *ngFor="let note of notes.reverse(); let i = index">
      <app-note [note]="note" (refresh)="getListOfNotes()"></app-note>
    </div>
  </div>
</div>
