import { FormBuilder, FormControl, AbstractControl } from '@angular/forms';
import { Component, Input } from '@angular/core';
import { AngularEditorConfig } from '@kolkov/angular-editor';
@Component({
  selector: 'app-note',
  templateUrl: './note.component.html',
  styleUrls: ['./note.component.scss'],
})
export class NoteComponent {
  @Input() id = '';
  @Input() form: AbstractControl = new FormControl();
  constructor(private fb: FormBuilder) {
  }

  editorConfig: AngularEditorConfig = {
    editable: true,
    spellcheck: true,
    height: '25rem',
    minHeight: '5rem',
    placeholder: 'Enter your notes here...',
    translate: 'no',
    width: '100%',
    toolbarPosition: 'bottom',
    outline: false,
    showToolbar: false,
    uploadWithCredentials: false, // if needed
    customClasses: [
      // optional
      {
        name: 'quote',
        class: 'quote',
      },
      {
        name: 'redText',
        class: 'redText',
      },
      {
        name: 'titleText',
        class: 'titleText',
        tag: 'h1',
      },
    ],
  };

  showToolbar() {
    this.editorConfig.showToolbar = true;
  }

  hideToolbar() {
    this.editorConfig.showToolbar = true;
  }
}
<span *ngIf="formControlName">
